          return response.text()
        })
        .then(text => {
          setdata(
            marked(`${text}`).trim()
            )
        })
        // setdata(Blog);
        // console.log()
        // setdata([])
        // console.log(slug)
      }, [data])

    return (
      <>
        {/* <article className="blog-container" dangerouslySetInnerHTML={{__html: data}}> */}

        {/* </article> */}
        <MarkdownPreview value={ data } transformImageUri={uri =>
          uri.startsWith("http")
            ? uri
            : `${process.env.IMAGE_BASE_URL}${uri}`
        }/>
        {/* <MarkdownPreview className="ui post content" value="#Hey !" /> */}


      </>

    )
}

export default Blog;
