import {useGlobalContext} from '../context'
import {useEffect,useState} from "react"
import {useParams} from 'react-router-dom';
import Prism from "prismjs"
import marked from 'marked';
// import from 
// import file1 from '../../Blogs/Html/Html4SimpleTages/Html4SimpleTages.js';

// import file from '../../Blogs/Html/Html4SimpleTages/Html4SimpleTages.md';

const Blog=()=>{



    const {BlogInfo}=useGlobalContext();
    const {blogname}  = useParams();
    const [data,setdata]=useState("");

    // console.log(BlogData)
    // let dic=BlogData.find((data)=>data.id===1) 

    useEffect(() => {
        Prism.highlightAll()
        let Blog=BlogInfo.find((data1)=>(data1.data).find(value=>value.name===blogname));
        Blog=Blog.data.find(value=>value.name===blogname);
        let link=Blog.link;
        console.log(link==='../../Blogs/Html/Html4SimpleTages/Html4SimpleTages.md')
        const file=require(__dirname+"/"+link);

        fetch(file)
        .then(response => {
          return response.text()
        })
        .then(text => {
          setdata(
            marked(`${text}`).trim()
            )
        })
        // setdata(Blog);
        // console.log()
        // setdata([])
        // console.log(slug)
      }, [data])

    return (
      <>
        <article className="blog-container" dangerouslySetInnerHTML={{__html: data}}>

        </article>
        <ReactMarkdown
        source={document.node.content.substring(0, 500).concat("...")}
        transformImageUri={uri =>
          uri.startsWith("http")
            ? uri
            : `${process.env.IMAGE_BASE_URL}${uri}`
        }
        className="indexArticle"
        escapeHtml={false}
      />
      </>

    )
}

export default Blog;
